import { useContext } from 'react'
import { GlobalStoreContext } from '../store'
function DeleteSongModal() {
    let title = "aaa";
    const { store } = useContext(GlobalStoreContext);
    function handleDeleteSong(){
        store.deleteSong()
        store.hideDeleteSong()
    }
    function handleHideDeleteSong(){
        store.hideDeleteSong()
    }
    if(store.markSongForDeletionId!=null){
        //title = store.currentList.songs[store.markSongForDeletionId].title;
    }
    title = store.markSongForDeletion.songs[store.markSongForDeletionId].title;

    return (
        <div
            class="modal"
            id="delete-song-modal"
            data-animation="slideInOutLeft">
                <div class="modal-root" id='verify-delete-song-root'>
                    <div class="modal-north">
                        Remove Song?
                    </div>
                    <div class="modal-center">
                        <div class="modal-center-content">
                            Are you sure you wish to permanently remove the <span>{title}</span> &nbsp;from the playlist?
                        </div>
                    </div>
                    <div class="modal-south">
                        <input type="button"
                            id="delete-song-confirm-button"
                            class="modal-button"
                            onClick={handleDeleteSong}
                            value='Confirm' />
                        <input type="button"
                            id="delete-song-cancel-button"
                            class="modal-button"
                            onClick={handleHideDeleteSong}
                            value='Cancel' />
                    </div>
                </div>
        </div>
    );
}

export default  DeleteSongModal;
